import React from 'react'
import useProductList from './product-list.hooks'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

const ProductCard = () => {
  const { productList } = useProductList()
  return (
    <div>
      {productList.map((item, index) => (
        <div value={item} key={index}>
          <Row>
            <Col>
              <div>
                <img style={{width: '50%'}} src={item.image} alt="bottle"/>
              </div>
              <h6>{item.name}</h6>
              <div>{item.country} | {item.region}</div>
              <div>{item.grapeVarieties} {item.vintageYear !== 0 ? item.vintageYear : 'Non-Vintage'}</div>
              <div>S${item.price}</div>
              <div style={{display: 'flex'}}>
                <button type="button">Add to Cart</button>
                <button type="button">Save</button>
              </div>
            </Col>
          </Row>
        </div>
      ))}
    </div>
//     alcohol: "13.5"
// bottleSize: "750"
// country: "France"
// grapeVarietes: "Cabernet Franc, Cabernet Sauvignon, Tannat"
// id: "263579"
