import React from 'react'
import { Link } from 'react-router-dom'
import AddToCartButton from '../AddToCartButton'
import BookmarkButton from '../BookmarkButton'
import useProductList from './product-list.hooks'
import Toast from 'react-bootstrap/Toast'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

const ProductCard = () => {
  const { productList, handleClick } = useProductList()

  return (
    <div>
      <Row className="justify-content-md-center">
        {productList.map((item, index) => (
          <Col md={4} value={item} key={index}>
            <Link to={`product/${item.id}`}>
              <Row style={{height: 'calc(90vh/3)'}}>
                <Col md={3}>
                  <div>
                    <img className="img-fluid" src={item.image} alt="bottle"/>
                  </div>
                </Col>
                <Col>
                  <div className="h5" style={{color: '#842529'}}>{item.name} {item.vintageYear !== 0 ? item.vintageYear : ''}</div>
                  <div>{item.type}</div>
                  <div>{item.vintageYear === 0 ? 'Non Vintage' : ''}</div>
                  <div>{item.country}, {item.region}</div>
                  <div>{item.grapeVarieties}</div>
                  <div>S${item.price}</div>
                  <Row className="gx-2" style={{display: 'flex'}}>
                    <AddToCartButton handleClickButton={handleClick} productData={item}/>
                    <BookmarkButton/>
                    {/* <button type="button">Save</button> */}
                  </Row>
                </Col>
              </Row>
            </Link>
          </Col>
        ))}
      </Row>
      <Toast>
        <Toast.Header>
          <img src="holder.js/20x20?text=%20" className="rounded mr-2" alt="" />
          <strong className="mr-auto">Bootstrap</strong>
          <small>11 mins ago</small>
        </Toast.Header>
        <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
      </Toast>
    </div>
//     alcohol: "13.5"
// bottleSize: "750"
// country: "France"
// grapeVarietes: "Cabernet Franc, Cabernet Sauvignon, Tannat"
// id: "263579"
// image: "https://portal.wine.delivery/media/catalog/product/2/6/263579_alain_brumont_chateau_bouscasse_2014.jpg"
// name: "Alain Brumont Château Bouscassé"
// price: 70
// producer: "Alain Brumont"
// qty: 0
// region: "Madiran"
// type: "Red Wine"
// vintageYear: "2014"
  )
}

export default ProductCard
