import React from 'react'
import useProductList from './product-list.hooks'
import { useHistory } from 'react-router-dom'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

const ProductCard = () => {
  const { productList } = useProductList()
  const history = useHistory()

  const handleClickDetail = (id) =>{ 
    let path = `/product/${id}`
    history.push(path)
  }
  return (
    <div>
      <Row>
        {productList.map((item, index) => (
          <Col onClick={handleClickDetail(item.id)} md={4} value={item} key={index}>
            <div>
              <img style={{width: '50%'}} src={item.image} alt="bottle"/>
            </div>
            <h6>{item.name}</h6>
            <div>{item.country} | {item.region}</div>
            <div>{item.grapeVarieties} {item.vintageYear !== 0 ? item.vintageYear : 'Non-Vintage'}</div>
            <div>S${item.price}</div>
            <div style={{display: 'flex'}}>
              <button type="button">Add to Cart</button>
              <button type="button">Save</button>
            </div>
          </Col>
        ))}
      </Row>
    </div>
//     alcohol: "13.5"
